import React, { useState, useEffect } from 'react';
import './UserProfile2.css';

const UserProfile = () => {
  // State declarations
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [imageFile, setImageFile] = useState(null);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [profiles, setProfiles] = useState([]);
  const [showAddProfileModal, setShowAddProfileModal] = useState(false);
  const [newProfileData, setNewProfileData] = useState({
    name: '',
    relation: '',
    phone: '',
    email: ''
  });

  // Add the missing handleLogout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  // Add the missing fetchUserProfile function
  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        window.location.href = '/login';
        return;
      }

      const response = await fetch('http://localhost:4000/profile', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        setFormData(data.user);
        setError(null);
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Failed to fetch profile');
        if (response.status === 401 || response.status === 403) {
          localStorage.removeItem('token');
          window.location.href = '/login';
        }
      }
    } catch (error) {
      setError('Network error occurred');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUserProfile();
  }, []);

  // Add the missing fetchProfiles function
  const fetchProfiles = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profiles', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setProfiles(data.profiles);
      }
    } catch (error) {
      console.error('Error fetching profiles:', error);
    }
  };

  // Add the missing handleAddProfile function
  const handleAddProfile = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profiles', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newProfileData)
      });

      if (response.ok) {
        const data = await response.json();
        setProfiles([...profiles, data.profile]);
        setShowAddProfileModal(false);
        setNewProfileData({
          name: '',
          relation: '',
          phone: '',
          email: ''
        });
      }
    } catch (error) {
      console.error('Error adding profile:', error);
    }
  };

  // Add the missing handleInputChange function
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Add the missing handleImageChange function
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
    }
  };

  // Add the missing uploadImage function
  const uploadImage = async () => {
    if (!imageFile) return;

    const formDataImg = new FormData();
    formDataImg.append('profileImage', imageFile);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/upload-image', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formDataImg
      });

      if (response.ok) {
        const data = await response.json();
        setUser(prev => ({
          ...prev,
          profileImage: data.imageUrl
        }));
        setImageFile(null);
      }
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  // Add the missing handleSubmit function
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/save', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setUser(formData);
        setEditing(false);
        if (imageFile) {
          await uploadImage();
        }
      }
    } catch (error) {
      console.error('Profile save error:', error);
    }
  };

  // Add the missing handlePasswordChange function
  const handlePasswordChange = async (e) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/change-password', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword
        })
      });

      if (response.ok) {
        alert('Password changed successfully!');
        setShowPasswordModal(false);
        setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
      }
    } catch (error) {
      console.error('Error changing password:', error);
    }
  };

  if (loading) {
    return <div className="profile-container">Loading...</div>;
  }

  if (error) {
    return <div className="profile-container">Error: {error}</div>;
  }

  if (!user) {
    return <div className="profile-container">No user data found</div>;
  }

  return (
    <div className="profile-container">
      {/* Sidebar Navigation */}
      <div className="profile-sidebar">
        <div className="sidebar-header">
          <h3>Family Profiles</h3>
          <button 
            className="add-profile-btn"
            onClick={() => setShowAddProfileModal(true)}
          >
            + Add Profile
          </button>
        </div>

        <div className="profile-list">
          {/* Main User Profile */}
          <div className="profile-item active">
            <div className="profile-avatar-small">
              {user.profileImage ? (
                <img src={user.profileImage} alt="Profile" />
              ) : (
                <div className="avatar-placeholder-small">ðŸ‘¤</div>
              )}
            </div>
            <div className="profile-info-small">
              <h4>{user.name}</h4>
              <p>Main Profile</p>
            </div>
          </div>

          {/* Other Profiles */}
          {profiles.map(profile => (
            <div className="profile-item" key={profile.id}>
              <div className="profile-avatar-small">
                {profile.profileImage ? (
                  <img src={profile.profileImage} alt="Profile" />
                ) : (
                  <div className="avatar-placeholder-small">ðŸ‘¤</div>
                )}
              </div>
              <div className="profile-info-small">
                <h4>{profile.name}</h4>
                <p>{profile.relation}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="profile-main-content">
        {/* Header */}
        <div className="profile-header">
          <div className="profile-title">
            <div className="user-icon">ðŸ‘¤</div>
            <h1>User Profile</h1>
          </div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <button className="logout-btn" onClick={handleLogout}>
              ðŸšª Logout
            </button>
          </div>
        </div>

        {/* Rest of your profile content would go here */}
        {/* ... */}

        {/* Add Profile Modal */}
        {showAddProfileModal && (
          <div className="modal-overlay">
            <div className="modal">
              <div className="modal-header">
                <h3>Add New Profile</h3>
                <button 
                  className="close-btn"
                  onClick={() => setShowAddProfileModal(false)}
                >
                  âœ•
                </button>
              </div>
              <form onSubmit={handleAddProfile}>
                <div className="form-group">
                  <label>Full Name</label>
                  <input
                    type="text"
                    value={newProfileData.name}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      name: e.target.value
                    })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Relation</label>
                  <select
                    value={newProfileData.relation}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      relation: e.target.value
                    })}
                    required
                  >
                    <option value="">Select Relation</option>
                    <option value="Spouse">Spouse</option>
                    <option value="Child">Child</option>
                    <option value="Parent">Parent</option>
                    <option value="Sibling">Sibling</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Phone Number</label>
                  <input
                    type="tel"
                    value={newProfileData.phone}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      phone: e.target.value
                    })}
                  />
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="email"
                    value={newProfileData.email}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      email: e.target.value
                    })}
                  />
                </div>
                <div className="modal-actions">
                  <button type="submit" className="save-btn">
                    Save Profile
                  </button>
                  <button 
                    type="button" 
                    className="cancel-btn"
                    onClick={() => setShowAddProfileModal(false)}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
