import React, { useState, useEffect } from 'react';
import './UserProfile2.css';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        window.location.href = '/login';
        return;
      }

      const response = await fetch('http://localhost:4000/profile', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        setFormData(data.user);
        setError(null);
      } else {
        if (response.status === 403 || response.status === 401) {
          localStorage.removeItem('token');
          window.location.href = '/login';
        } else {
          setError('Failed to fetch profile');
        }
      }
    } catch (error) {
      setError('Network error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');

    try {
      const response = await fetch('http://localhost:4000/profile/save', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setUser(formData);
        setEditing(false);
        alert('Profile saved successfully!');
      } else {
        const error = await response.json();
        alert(error.error || 'Failed to save profile');
      }
    } catch (err) {
      alert('Something went wrong');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-card">
          <div className="spinner"></div>
          <div className="loading-text">Loading profile...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-card">
          <div className="error-icon">‚ùå</div>
          <h3 className="error-title">Error: {error}</h3>
          <button 
            onClick={() => {
              setError(null);
              setLoading(true);
              fetchUserProfile();
            }} 
            className="retry-btn"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="error-container">
        <div className="error-card">
          <div className="error-icon">üòï</div>
          <div className="error-title">Failed to load profile</div>
          <button 
            onClick={() => window.location.reload()} 
            className="retry-btn"
          >
            Reload Page
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      {/* Navigation */}
      <nav className="navbar">
        <div className="nav-container">
          <div className="nav-left">
            <div className="logo">NumPath</div>
          </div>
          <div className="nav-right">
            <span className="welcome-text">Welcome, {user.name}</span>
            <button className="logout-btn" onClick={handleLogout}>
              üö™ Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="main-container">
        {/* Profile Header */}
        <div className="profile-header-card">
          <div className="profile-bg"></div>
          <div className="profile-content">
            <div className="profile-avatar">
              <div className="avatar-icon">üë§</div>
            </div>
            <h1 className="profile-name">{user.name}</h1>
            <p className="profile-email">{user.email}</p>
            <button 
              className={`edit-toggle-btn ${editing ? 'cancel' : 'edit'}`}
              onClick={() => setEditing(!editing)}
            >
              {editing ? '‚ùå Cancel Edit' : '‚úèÔ∏è Edit Profile'}
            </button>
          </div>
        </div>

        {/* Profile Form */}
        <div className="profile-form-card">
          <h2 className="form-title">
            <span className="form-icon">üìã</span>
            Personal Information
          </h2>

          <form onSubmit={handleSubmit} className="profile-form">
            <div className="form-grid">
              <div className="form-group">
                <label className="form-label">
                  üë§ Full Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={formData.name || ''}
                  onChange={handleInputChange}
                  disabled={!editing}
                  className={`form-input ${editing ? 'editable' : 'disabled'}`}
                />
              </div>

              <div className="form-group">
                <label className="form-label">
                  üìß Email Address
                </label>
                <input
                  type="email"
                  value={user.email}
                  disabled
                  className="form-input disabled"
                />
              </div>

              <div className="form-group">
                <label className="form-label">
                  üì± Phone Number
                </label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone || ''}
                  onChange={handleInputChange}
                  disabled={!editing}
                  placeholder="+91 xxxxx-xxxxx"
                  className={`form-input ${editing ? 'editable' : 'disabled'}`}
                />
              </div>

              <div className="form-group">
                <label className="form-label">
                  üìÖ Date of Birth
                </label>
                <input
                  type="date"
                  name="dateOfBirth"
                  value={formData.dateOfBirth ? formData.dateOfBirth.split('T')[0] : ''}
                  onChange={handleInputChange}
                  disabled={!editing}
                  className={`form-input ${editing ? 'editable' : 'disabled'}`}
                />
              </div>

              <div className="form-group full-width">
                <label className="form-label">
                  üè† Address
                </label>
                <textarea
                  name="address"
                  value={formData.address || ''}
                  onChange={handleInputChange}
                  disabled={!editing}
                  rows="3"
                  placeholder="Your address"
                  className={`form-textarea ${editing ? 'editable' : 'disabled'}`}
                />
              </div>

              <div className="form-group">
                <label className="form-label">
                  üíº Occupation
                </label>
                <input
                  type="text"
                  name="occupation"
                  value={formData.occupation || ''}
                  onChange={handleInputChange}
                  disabled={!editing}
                  placeholder="Your occupation"
                  className={`form-input ${editing ? 'editable' : 'disabled'}`}
                />
              </div>

              <div className="form-group">
                <label className="form-label">
                  üìù Bio
                </label>
                <textarea
                  name="bio"
                  value={formData.bio || ''}
                  onChange={handleInputChange}
                  disabled={!editing}
                  rows="4"
                  placeholder="Tell us about yourself"
                  className={`form-textarea ${editing ? 'editable' : 'disabled'}`}
                />
              </div>
            </div>

            {editing && (
              <div className="form-actions">
                <button type="submit" className="save-btn">
                  üíæ Save Changes
                </button>
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
