import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const UserProfile = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [updating, setUpdating] = useState(false);
  const [userProfile, setUserProfile] = useState({
    name: "",
    email: "",
    phone: "",
    dateOfBirth: "",
    gender: "",
    address: {
      street: "",
      city: "",
      state: "",
      pincode: "",
      country: "India",
    },
    occupation: "",
    bio: "",
    birthPlace: "",
  });

  // Numerology calculations state
  const [numerologyProfile, setNumerologyProfile] = useState(null);

  // Calculate numerology numbers
  const calculateNumerology = (name, dateOfBirth) => {
    if (!name || !dateOfBirth) return null;

    // Helper function to reduce numbers to single digit
    const reduceToSingleDigit = (num) => {
      while (num > 9 && num !== 11 && num !== 22 && num !== 33) {
        num = num.toString().split('').reduce((sum, digit) => sum + parseInt(digit), 0);
      }
      return num;
    };

    // Calculate Life Path Number
    const date = new Date(dateOfBirth);
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();

    const lifePathSum = day + month + year;
    const lifePathNumber = reduceToSingleDigit(lifePathSum);

    // Calculate Birth Number (day of birth)
    const birthNumber = reduceToSingleDigit(day);

    // Calculate Destiny Number (full name)
    const letterValues = {
      A: 1, B: 2, C: 3, D: 4, E: 5, F: 6, G: 7, H: 8, I: 9,
      J: 1, K: 2, L: 3, M: 4, N: 5, O: 6, P: 7, Q: 8, R: 9,
      S: 1, T: 2, U: 3, V: 4, W: 5, X: 6, Y: 7, Z: 8
    };

    const nameSum = name.toUpperCase().replace(/[^A-Z]/g, '').split('')
      .reduce((sum, letter) => sum + (letterValues[letter] || 0), 0);
    const destinyNumber = reduceToSingleDigit(nameSum);

    // Calculate Personal Year (current year + birth month + birth day)
    const currentYear = new Date().getFullYear();
    const personalYearSum = currentYear + month + day;
    const personalYear = reduceToSingleDigit(personalYearSum);

    return {
      lifePathNumber,
      birthNumber,
      destinyNumber,
      personalYear,
      calculations: {
        lifePathSum,
        nameSum,
        personalYearSum
      }
    };
  };

  // Update numerology when profile changes
  useEffect(() => {
    if (userProfile.name && userProfile.dateOfBirth) {
      const numerology = calculateNumerology(userProfile.name, userProfile.dateOfBirth);
      setNumerologyProfile(numerology);
    }
  }, [userProfile.name, userProfile.dateOfBirth]);

  // Number meanings
  const getNumberMeaning = (number, type) => {
    const meanings = {
      lifePathNumber: {
        1: "Leader, Independent, Pioneering",
        2: "Cooperative, Diplomatic, Sensitive",
        3: "Creative, Expressive, Optimistic",
        4: "Practical, Hardworking, Reliable",
        5: "Adventurous, Freedom-loving, Dynamic",
        6: "Nurturing, Responsible, Caring",
        7: "Spiritual, Analytical, Introspective",
        8: "Ambitious, Material success, Powerful",
        9: "Humanitarian, Generous, Compassionate",
        11: "Intuitive, Inspirational, Enlightened",
        22: "Master Builder, Visionary, Practical idealist",
        33: "Master Teacher, Compassionate, Healing"
      },
      birthNumber: {
        1: "Natural leader, Independent",
        2: "Cooperative, Peace-maker",
        3: "Creative, Artistic, Social",
        4: "Organized, Methodical, Stable",
        5: "Adventurous, Versatile, Free-spirited",
        6: "Nurturing, Family-oriented, Responsible",
        7: "Thoughtful, Spiritual, Analytical",
        8: "Ambitious, Business-minded, Successful",
        9: "Humanitarian, Generous, Wise"
      },
      destinyNumber: {
        1: "Destined to lead and innovate",
        2: "Destined to cooperate and harmonize",
        3: "Destined to create and inspire",
        4: "Destined to build and organize",
        5: "Destined to explore and experience",
        6: "Destined to nurture and heal",
        7: "Destined to seek truth and wisdom",
        8: "Destined for material achievement",
        9: "Destined to serve humanity"
      }
    };

    return meanings[type]?.[number] || "Unique spiritual path";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log("Profile updated:", userProfile);
  };

  return (
    <div style={{ 
      minHeight: "100vh", 
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      padding: "20px 0"
    }}>
      <div style={{ maxWidth: "1200px", margin: "0 auto", padding: "0 20px" }}>

        {/* Profile Form Section */}
        <div style={{
          background: "rgba(255, 255, 255, 0.1)",
          backdropFilter: "blur(10px)",
          borderRadius: "20px",
          padding: "30px",
          marginBottom: "30px",
          border: "1px solid rgba(255, 255, 255, 0.2)"
        }}>
          <div style={{ display: "flex", alignItems: "center", marginBottom: "30px" }}>
            <div style={{
              width: "50px",
              height: "50px",
              background: "linear-gradient(45deg, #ff6b6b, #ffa500)",
              borderRadius: "50%",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              marginRight: "15px"
            }}>
              ðŸ“Š
            </div>
            <h2 style={{ 
              color: "#fff", 
              margin: 0,
              fontSize: "28px",
              fontWeight: "600"
            }}>
              Get Your Complete Numerology Report
            </h2>
          </div>

          <p style={{ color: "rgba(255, 255, 255, 0.8)", marginBottom: "30px" }}>
            Quick calculation - Enter your details below
          </p>

          <form onSubmit={handleSubmit}>
            <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", gap: "20px" }}>

              {/* Month Selection */}
              <div>
                <label style={{ color: "#fff", display: "block", marginBottom: "8px" }}>Month</label>
                <select
                  style={{
                    width: "100%",
                    padding: "12px",
                    borderRadius: "10px",
                    border: "none",
                    background: "rgba(255, 255, 255, 0.1)",
                    color: "#fff",
                    fontSize: "16px"
                  }}
                  onChange={(e) => {
                    const month = e.target.value;
                    const currentDate = userProfile.dateOfBirth ? new Date(userProfile.dateOfBirth) : new Date();
                    currentDate.setMonth(parseInt(month) - 1);
                    setUserProfile({
                      ...userProfile,
                      dateOfBirth: currentDate.toISOString().split('T')[0]
                    });
                  }}
                >
                  <option value="">Select Month</option>
                  {Array.from({length: 12}, (_, i) => (
                    <option key={i+1} value={i+1}>
                      {new Date(0, i).toLocaleString('default', { month: 'long' })}
                    </option>
                  ))}
                </select>
              </div>

              {/* Day Input */}
              <div>
                <label style={{ color: "#fff", display: "block", marginBottom: "8px" }}>Day</label>
                <input
                  type="number"
                  min="1"
                  max="31"
                  placeholder="DD"
                  style={{
                    width: "100%",
                    padding: "12px",
                    borderRadius: "10px",
                    border: "none",
                    background: "rgba(255, 255, 255, 0.1)",
                    color: "#fff",
                    fontSize: "16px"
                  }}
                  onChange={(e) => {
                    const day = e.target.value;
                    const currentDate = userProfile.dateOfBirth ? new Date(userProfile.dateOfBirth) : new Date();
                    currentDate.setDate(parseInt(day));
                    setUserProfile({
                      ...userProfile,
                      dateOfBirth: currentDate.toISOString().split('T')[0]
                    });
                  }}
                />
              </div>

              {/* Year Input */}
              <div>
                <label style={{ color: "#fff", display: "block", marginBottom: "8px" }}>Year</label>
                <input
                  type="number"
                  min="1900"
                  max="2024"
                  placeholder="YYYY"
                  style={{
                    width: "100%",
                    padding: "12px",
                    borderRadius: "10px",
                    border: "none",
                    background: "rgba(255, 255, 255, 0.1)",
                    color: "#fff",
                    fontSize: "16px"
                  }}
                  onChange={(e) => {
                    const year = e.target.value;
                    const currentDate = userProfile.dateOfBirth ? new Date(userProfile.dateOfBirth) : new Date();
                    currentDate.setFullYear(parseInt(year));
                    setUserProfile({
                      ...userProfile,
                      dateOfBirth: currentDate.toISOString().split('T')[0]
                    });
                  }}
                />
              </div>

              {/* Full Name Input */}
              <div>
                <label style={{ color: "#fff", display: "block", marginBottom: "8px" }}>Full Name</label>
                <input
                  type="text"
                  placeholder="Birth name"
                  value={userProfile.name}
                  onChange={(e) => setUserProfile({...userProfile, name: e.target.value})}
                  style={{
                    width: "100%",
                    padding: "12px",
                    borderRadius: "10px",
                    border: "none",
                    background: "rgba(255, 255, 255, 0.1)",
                    color: "#fff",
                    fontSize: "16px"
                  }}
                />
              </div>
            </div>

            <button
              type="submit"
              style={{
                background: "linear-gradient(45deg, #ff6b6b, #ffa500)",
                color: "#fff",
                border: "none",
                padding: "15px 30px",
                borderRadius: "25px",
                fontSize: "16px",
                fontWeight: "600",
                marginTop: "20px",
                cursor: "pointer",
                display: "flex",
                alignItems: "center",
                gap: "10px"
              }}
            >
              âœ¨ Calculate My Numerology Profile
            </button>
          </form>
        </div>

        {/* Numerology Profile Display Section */}
        {numerologyProfile && (
          <div style={{
            background: "rgba(255, 255, 255, 0.1)",
            backdropFilter: "blur(10px)",
            borderRadius: "20px",
            padding: "30px",
            border: "1px solid rgba(255, 255, 255, 0.2)"
          }}>
            <div style={{ display: "flex", alignItems: "center", marginBottom: "30px" }}>
              <div style={{
                width: "50px",
                height: "50px",
                background: "linear-gradient(45deg, #4ecdc4, #44a08d)",
                borderRadius: "50%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                marginRight: "15px"
              }}>
                ðŸ”®
              </div>
              <h2 style={{ 
                color: "#fff", 
                margin: 0,
                fontSize: "28px",
                fontWeight: "600"
              }}>
                Your Numerology Profile
              </h2>
            </div>

            <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", gap: "20px" }}>

              {/* Life Path Number */}
              <div style={{
                background: "rgba(255, 255, 255, 0.1)",
                borderRadius: "15px",
                padding: "20px",
                textAlign: "center"
              }}>
                <div style={{
                  width: "60px",
                  height: "60px",
                  background: "linear-gradient(45deg, #ff6b6b, #ffa500)",
                  borderRadius: "50%",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  margin: "0 auto 15px",
                  fontSize: "24px",
                  fontWeight: "bold",
                  color: "#fff"
                }}>
                  {numerologyProfile.lifePathNumber}
                </div>
                <h3 style={{ color: "#fff", margin: "0 0 10px 0" }}>Life Path Number</h3>
                <p style={{ color: "rgba(255, 255, 255, 0.8)", fontSize: "14px", margin: 0 }}>
                  {getNumberMeaning(numerologyProfile.lifePathNumber, 'lifePathNumber')}
                </p>
              </div>

              {/* Birth Number */}
              <div style={{
                background: "rgba(255, 255, 255, 0.1)",
                borderRadius: "15px",
                padding: "20px",
                textAlign: "center"
              }}>
                <div style={{
                  width: "60px",
                  height: "60px",
                  background: "linear-gradient(45deg, #4ecdc4, #44a08d)",
                  borderRadius: "50%",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  margin: "0 auto 15px",
                  fontSize: "24px",
                  fontWeight: "bold",
                  color: "#fff"
                }}>
                  {numerologyProfile.birthNumber}
                </div>
                <h3 style={{ color: "#fff", margin: "0 0 10px 0" }}>Birth Number</h3>
                <p style={{ color: "rgba(255, 255, 255, 0.8)", fontSize: "14px", margin: 0 }}>
                  {getNumberMeaning(numerologyProfile.birthNumber, 'birthNumber')}
                </p>
              </div>

              {/* Destiny Number */}
              <div style={{
                background: "rgba(255, 255, 255, 0.1)",
                borderRadius: "15px",
                padding: "20px",
                textAlign: "center"
              }}>
                <div style={{
                  width: "60px",
                  height: "60px",
                  background: "linear-gradient(45deg, #a18cd1, #fbc2eb)",
                  borderRadius: "50%",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  margin: "0 auto 15px",
                  fontSize: "24px",
                  fontWeight: "bold",
                  color: "#fff"
                }}>
                  {numerologyProfile.destinyNumber}
                </div>
                <h3 style={{ color: "#fff", margin: "0 0 10px 0" }}>Destiny Number</h3>
                <p style={{ color: "rgba(255, 255, 255, 0.8)", fontSize: "14px", margin: 0 }}>
                  {getNumberMeaning(numerologyProfile.destinyNumber, 'destinyNumber')}
                </p>
              </div>

              {/* Personal Year */}
              <div style={{
                background: "rgba(255, 255, 255, 0.1)",
                borderRadius: "15px",
                padding: "20px",
                textAlign: "center"
              }}>
                <div style={{
                  width: "60px",
                  height: "60px",
                  background: "linear-gradient(45deg, #f093fb, #f5576c)",
                  borderRadius: "50%",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  margin: "0 auto 15px",
                  fontSize: "24px",
                  fontWeight: "bold",
                  color: "#fff"
                }}>
                  {numerologyProfile.personalYear}
                </div>
                <h3 style={{ color: "#fff", margin: "0 0 10px 0" }}>Personal Year 2025</h3>
                <p style={{ color: "rgba(255, 255, 255, 0.8)", fontSize: "14px", margin: 0 }}>
                  This year brings opportunities for {numerologyProfile.personalYear === 1 ? "new beginnings" : 
                  numerologyProfile.personalYear === 2 ? "cooperation" :
                  numerologyProfile.personalYear === 3 ? "creativity" :
                  numerologyProfile.personalYear === 4 ? "building foundations" :
                  numerologyProfile.personalYear === 5 ? "change and freedom" :
                  numerologyProfile.personalYear === 6 ? "family and responsibility" :
                  numerologyProfile.personalYear === 7 ? "spiritual growth" :
                  numerologyProfile.personalYear === 8 ? "material success" :
                  "completion and wisdom"}
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Navigation Tabs */}
        <div style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "10px",
          marginTop: "30px",
          justifyContent: "center"
        }}>
          {[
            { icon: "â­", text: "All Sections", active: true },
            { icon: "â“", text: "What is Numerology" },
            { icon: "â­", text: "Why Important" },
            { icon: "ðŸ›¤ï¸", text: "Life Path" },
            { icon: "ðŸ“…", text: "Birth Number" },
            { icon: "ðŸŽ¯", text: "Destiny Number" },
            { icon: "ðŸ”„", text: "Personal Cycles" }
          ].map((tab, index) => (
            <button
              key={index}
              style={{
                background: tab.active ? "rgba(255, 255, 255, 0.2)" : "rgba(255, 255, 255, 0.1)",
                color: "#fff",
                border: "1px solid rgba(255, 255, 255, 0.2)",
                borderRadius: "20px",
                padding: "10px 20px",
                fontSize: "14px",
                cursor: "pointer",
                display: "flex",
                alignItems: "center",
                gap: "8px"
              }}
            >
              {tab.icon} {tab.text}
            </button>
          ))}
        </div>

        {/* What is Numerology Section */}
        <div style={{
          background: "rgba(255, 255, 255, 0.1)",
          backdropFilter: "blur(10px)",
          borderRadius: "20px",
          padding: "30px",
          marginTop: "30px",
          border: "1px solid rgba(255, 255, 255, 0.2)"
        }}>
          <div style={{ display: "flex", alignItems: "center", marginBottom: "20px" }}>
            <div style={{
              width: "50px",
              height: "50px",
              background: "linear-gradient(45deg, #667eea, #764ba2)",
              borderRadius: "50%",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              marginRight: "15px"
            }}>
              â“
            </div>
            <h2 style={{ color: "#fff", margin: 0, fontSize: "24px" }}>What is Numerology?</h2>
          </div>
          <p style={{ color: "rgba(255, 255, 255, 0.8)", lineHeight: "1.6", margin: 0 }}>
            Numerology is an ancient metaphysical science that explores the mystical relationship between numbers and life events. 
            It's based on the belief that numbers carry vibrational energy that influences your personality, relationships, and life path. 
            By analyzing your birth date and name, numerology reveals insights about your character, talents, challenges, and destiny.
          </p>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
