import React, { useState, useEffect } from 'react';
import './UserProfile.css';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [imageFile, setImageFile] = useState(null);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        setFormData(data.user);
      } else {
        console.error('Failed to fetch profile');
      }
    } catch (error) {
      console.error('Error fetching profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
    }
  };

  const uploadImage = async () => {
    if (!imageFile) return;

    const formDataImg = new FormData();
    formDataImg.append('profileImage', imageFile);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/upload-image', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formDataImg
      });

      if (response.ok) {
        const data = await response.json();
        setUser(prev => ({
          ...prev,
          profileImage: data.imageUrl
        }));
        setImageFile(null);
        alert('Profile image updated successfully!');
      }
    } catch (error) {
      console.error('Error uploading image:', error);
      alert('Failed to upload image');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setUser(formData);
        setEditing(false);
        alert('Profile updated successfully!');

        // Upload image if selected
        if (imageFile) {
          await uploadImage();
        }
      } else {
        alert('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile');
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/change-password', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword
        })
      });

      if (response.ok) {
        alert('Password changed successfully!');
        setShowPasswordModal(false);
        setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
      } else {
        const error = await response.json();
        alert(error.error || 'Failed to change password');
      }
    } catch (error) {
      console.error('Error changing password:', error);
      alert('Error changing password');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const downloadData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/export', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'user-data.json';
        a.click();
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Error downloading data:', error);
    }
  };

  const deleteAccount = async () => {
    const password = window.prompt('Please enter your password to confirm account deletion:');
    if (!password) return;

    const confirmed = window.confirm('Are you sure you want to delete your account? This action cannot be undone.');
    if (!confirmed) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/delete-account', {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ password })
      });

      if (response.ok) {
        alert('Account deleted successfully');
        localStorage.removeItem('token');
        window.location.href = '/';
      } else {
        const error = await response.json();
        alert(error.error || 'Failed to delete account');
      }
    } catch (error) {
      console.error('Error deleting account:', error);
      alert('Error deleting account');
    }
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading">Loading profile...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="profile-container">
        <div className="error">Failed to load profile</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      {/* Header */}
      <div className="profile-header">
        <div className="profile-title">
          <div className="user-icon"></div>
          <h1>User Profile</h1>
        </div>
        <button className="logout-btn" onClick={handleLogout}>
           Logout
        </button>
      </div>

      {/* Profile Banner */}
      <div className="profile-banner">
        <div className="profile-avatar-section">
          <div className="profile-avatar">
            {user.profileImage ? (
              <img src={user.profileImage} alt="Profile" />
            ) : (
              <div className="avatar-placeholder"></div>
            )}
          </div>
          <div className="profile-basic-info">
            <h2>{user.name}</h2>
            <p className="email">@{user.email}</p>
          </div>
        </div>
      </div>

      {/* Personal Information */}
      <div className="profile-section">
        <div className="section-header">
          <h3>Personal Information</h3>
          <button 
            className="edit-btn"
            onClick={() => setEditing(!editing)}
          >
             {editing ? 'Cancel' : 'Edit Profile'}
          </button>
        </div>

        <form onSubmit={handleSubmit} className="profile-form">
          <div className="form-grid">
            <div className="form-group">
              <label> Full Name</label>
              <input
                type="text"
                name="name"
                value={formData.name || ''}
                onChange={handleInputChange}
                disabled={!editing}
                required
              />
            </div>

            <div className="form-group">
              <label> Email Address</label>
              <input
                type="email"
                value={user.email}
                disabled
                className="disabled-field"
              />
            </div>

            <div className="form-group">
              <label> Phone Number</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone || ''}
                onChange={handleInputChange}
                disabled={!editing}
                placeholder="+91 xxx-xxx-xxxx"
              />
            </div>

            <div className="form-group">
              <label> Date of Birth</label>
              <input
                type="date"
                name="dateOfBirth"
                value={formData.dateOfBirth ? formData.dateOfBirth.split('T')[0] : ''}
                onChange={handleInputChange}
                disabled={!editing}
              />
            </div>

            <div className="form-group">
              <label> Address</label>
              <textarea
                name="address"
                value={formData.address || ''}
                onChange={handleInputChange}
                disabled={!editing}
                rows="3"
                placeholder="Your address"
              />
            </div>

            <div className="form-group">
              <label> Occupation</label>
              <input
                type="text"
                name="occupation"
                value={formData.occupation || ''}
                onChange={handleInputChange}
                disabled={!editing}
                placeholder="Your occupation"
              />
            </div>
          </div>

          <div className="form-group full-width">
            <label> Bio</label>
            <textarea
              name="bio"
              value={formData.bio || ''}
              onChange={handleInputChange}
              disabled={!editing}
              rows="4"
              placeholder="Tell us about yourself"
            />
          </div>

          {editing && (
            <div className="form-group full-width">
              <label> Profile Image</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                className="file-input"
              />
              {imageFile && (
                <p className="file-selected">Selected: {imageFile.name}</p>
              )}
            </div>
          )}

          {editing && (
            <div className="form-actions">
              <button type="submit" className="save-btn">
                 Save Changes
              </button>
            </div>
          )}
        </form>
      </div>

      {/* Account Actions */}
      <div className="profile-section">
        <h3>Account Actions</h3>
        <div className="action-buttons">
          <button 
            className="action-btn change-password"
            onClick={() => setShowPasswordModal(true)}
          >
             Change Password
          </button>
          <button className="action-btn privacy-settings">
             Privacy Settings
          </button>
          <button 
            className="action-btn download-data"
            onClick={downloadData}
          >
             Download Data
          </button>
          <button 
            className="action-btn delete-account"
            onClick={deleteAccount}
          >
             Delete Account
          </button>
        </div>
      </div>

      {/* Password Change Modal */}
      {showPasswordModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Change Password</h3>
              <button 
                className="close-btn"
                onClick={() => setShowPasswordModal(false)}
              >

              </button>
            </div>
            <form onSubmit={handlePasswordChange}>
              <div className="form-group">
                <label>Current Password</label>
                <input
                  type="password"
                  value={passwordData.currentPassword}
                  onChange={(e) => setPasswordData(prev => ({
                    ...prev,
                    currentPassword: e.target.value
                  }))}
                  required
                />
              </div>
              <div className="form-group">
                <label>New Password</label>
                <input
                  type="password"
                  value={passwordData.newPassword}
                  onChange={(e) => setPasswordData(prev => ({
                    ...prev,
                    newPassword: e.target.value
                  }))}
                  required
                  minLength="6"
                />
              </div>
              <div className="form-group">
                <label>Confirm New Password</label>
                <input
                  type="password"
                  value={passwordData.confirmPassword}
                  onChange={(e) => setPasswordData(prev => ({
                    ...prev,
                    confirmPassword: e.target.value
                  }))}
                  required
                  minLength="6"
                />
              </div>
              <div className="modal-actions">
                <button type="submit" className="save-btn">
                  Change Password
                </button>
                <button 
                  type="button" 
                  className="cancel-btn"
                  onClick={() => setShowPasswordModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
